import React, { component } from "react";
import Coin from "./Coin";

//map over coin and create new instances of each coin
//recieves array, maps over array and creates instance?

const Coins = (props) => {
  return (
    <>
      {props.coins.map((coin) => {
        return (
          <Coins
            id={coin.id}
            symbol={coin.symbol}
            marketCap={coin.market_cap}
            coinPerCent={coin.coinpercent}
            coinPrice={coin.current_price}
            image={coin.image}
          />
        );
      })}
    </>
  );
};

export default Coins;
