import { useEffect, useState } from "react";
import axios from "axios";
import { FaCoins } from "react-icons/fa";
import Coins from "./components/Coins";
import "./App.css";
import Pages from "./components/Pages";
import { Route, Routes } from "react-router-dom";
import CoinDescription from "./descriptioncomponents/CoinDescription";
import Description from "./descriptioncomponents/Description";
import Navbar from "./components/Navbar";

function App() {
  const [coins, setCoins] = useState([]);
  const [page, setPage] = useState(1);

  useEffect(() => {
    async function getApiData() {
      try {
        const res = await axios.get(
          `https://api.coingecko.com/api/v3/coins/markets?vs_currency=gbp&order=market_cap_desc&per_page=5&page=${page}&sparkline=false`
        );
        console.log(res.data);
        setCoins(res.data);
        const now = Math.round(Date.now() / 1000);
        const sevenDaysAgo = Math.round(now - 86400);
        for (let index = 0; index < res.data.length; index++) {
          const element = res.data[index];
          const history = await axios.get(
            `https://api.coingecko.com/api/v3/coins/${element.id}/market_chart/range?vs_currency=gbp&from=${sevenDaysAgo}&to=${now}`
          );
          //each entry will have correct data attached to it
          res.data[index].history = history.data;
        }
        console.log("console log", res.data);
      } catch (error) {
        console.log("Error", error);
      }
    }
    getApiData();
  }, []);

  return (
    <>
      {/* <h1 className="header">
        <FaCoins />
        Crypto Tracker Api
      </h1> */}
      <Navbar />
      <h2>Search Currency</h2>
      <input type="text"></input>
      {/* <Coins coins={coins} />
      <Pages pages={page} /> */}

      <Routes>
        <Route path="coin-description" element={<CoinDescription />} />
      </Routes>
    </>
  );
}

export default App;
