  //       // console.log("console log", res.data);
  //     } catch (error) {
  //       console.log("Error", error);
  //     }
  //   }
  //   getDescriptionApiData();
  // }, []);

  return (
    <>
      <Navbar />
      <Coins coins={coins} />

      {/* <Routes>
        <Route path="coin-description" element={<Description />} />
      </Routes> */}
    </>
  );
}

export default App;
